INSERT [dbo].[Hotels] ([hotel_id], [host_id], [hotel_name], [hotel_image_url], [address], [location_id], [latitude], [longitude], [rating], [status], [description], [policy]) VALUES (2, 8, N'Lotte Hotel Hanoi', N'https://pix8.agoda.net/hotelImages/665815/-1/7bf78f95a19a572557250258b29ceffa.jpg?ce=0&s=1024x768', N'54 Liễu Giai', 1, 21.0314408060538, 105.81238925457, CAST(4.6 AS Decimal(3, 1)), N'active', N'<b>Lotte Hotel Hanoi - Nơi lưu giữ huyền thoại</b><br><br>Lotte Hotel Hanoi, một khách sạn 5 sao nổi tiếng tọa lạc tại trung tâm Hà Nội, Việt Nam, là một biểu tượng của sự xa hoa và sang trọng. Với 318 phòng nghỉ được trang bị đầy đủ tiện nghi hiện đại, khách sạn này mang đến cho du khách một trải nghiệm đích thực trong không gian lịch sự và tiện nghi. Lotte Hotel Hanoi đã được xây dựng vào năm 2014 và được cải tạo lại cùng năm đó, đảm bảo rằng khách hàng sẽ được tận hưởng những tiện ích và trang thiết bị mới nhất.

