INSERT [dbo].[Hotels] ([hotel_id], [host_id], [hotel_name], [hotel_image_url], [address], [location_id], [latitude], [longitude], [rating], [status], [description], [policy]) VALUES (23, 14, N'Saigon Morin Hotel', N'https://pix8.agoda.net/hotelImages/11001/-1/7308bb40bc16a01bb02c6ee794050b00.jpg?ca=0&ce=1&s=1024x768', N'30 Lê lợi', 6, 16.4670671343649, 107.590484619141, CAST(3.7 AS Decimal(3, 1)), N'active', N'<b>Khách sạn Saigon Morin - Nơi kỷ niệm lịch sử và sự sang trọng tại Huế</b><br><br>Khách sạn Saigon Morin, một khách sạn 4.5 sao tọa lạc tại trung tâm thành phố Huế, Việt Nam, là một điểm đến lý tưởng cho du khách mong muốn khám phá vẻ đẹp lịch sử và văn hóa của thành phố cố đô. Với lịch sử hơn 100 năm, khách sạn được xây dựng từ năm 1901 và đã trải qua một cuộc cải tạo năm 2011, Saigon Morin Hotel mang đến không chỉ sự sang trọng mà còn là một phần của di sản văn hóa Huế.

