INSERT [dbo].[Hotels] ([hotel_id], [host_id], [hotel_name], [hotel_image_url], [address], [location_id], [latitude], [longitude], [rating], [status], [description], [policy]) VALUES (28, 6, N'Genesis Regal Cruises', N'https://pix8.agoda.net/hotelImages/6409135/-1/afffc7afae3a708b6ae0be2c05103011.jpg?ce=0&s=1024x768', N'Lô 19, Cảng Quốc Tế Tuần Châu, Hạ Long, Quảng Ninh', 7, 20.9226016998291, 106.98245239257813, CAST(4.8 AS Decimal(3, 1)), N'active', N'<b>Genesis Regal Cruises - Khám phá vẻ đẹp Hạ Long trên du thuyền sang trọng</b><br><br>Genesis Regal Cruises là một khách sạn 5 sao nằm tại Hạ Long, Việt Nam. Với thiết kế sang trọng và dịch vụ chất lượng, khách sạn này là sự lựa chọn tuyệt vời cho những ai muốn khám phá vẻ đẹp tuyệt vời của Hạ Long từ một du thuyền đẳng cấp.

