INSERT [dbo].[Hotels] ([hotel_id], [host_id], [hotel_name], [hotel_image_url], [address], [location_id], [latitude], [longitude], [rating], [status], [description], [policy]) VALUES (50, 14, N'Terracotta Hotel and Resort Dalat', N'https://pix8.agoda.net/hotelImages/836781/0/a0edc0b6d5e4d8d6a87e906f68f1243a.jpg?ce=2&s=1024x768', N'Phân Khu Chức Năng 7.9, KDL Hồ Tuyền Lâm, P.3, Đà Lạt, VN', 12, 11.894785, 108.43811, CAST(3.7 AS Decimal(3, 1)), N'active', N'<b>Terracotta Hotel and Resort Dalat - Nơi lưu giữ vẻ đẹp của Đà Lạt</b><br><br>Terracotta Hotel and Resort Dalat là một khách sạn 4.5 sao nằm tại thành phố Đà Lạt, Việt Nam. Với vị trí thuận lợi và tiện nghi hiện đại, khách sạn này là lựa chọn lý tưởng cho những ai muốn khám phá vẻ đẹp của thành phố hoa Đà Lạt.

